curl -H "Content-Type: application/json" \
    -H "Authorization: Bearer $TOKEN" \
    -X POST -d '{
      "kernel": "linode/latest-64bit",
      "comments": "This is my main Config",
      "memory_limit": 2048,
      "run_level": "default",
      "virt_mode": "paravirt",
      "interfaces": [
        {
          "purpose": "public",
          "label": "",
          "ipam_address": ""
        },
        {
          "purpose": "vlan",
          "label": "vlan-1",
          "ipam_address": "10.0.0.1/24"
        }
      ],
      "helpers": {
        "updatedb_disabled": true,
        "distro": true,
        "modules_dep": true,
        "network": true,
        "devtmpfs_automount": false
      },
      "label": "My Config",
      "devices": {
        "sda": {
          "disk_id": 123456,
          "volume_id": null
        },
        "sdb": {
          "disk_id": 123457,
          "volume_id": null
        }
      }
    }' \
    https://api.linode.com/v4/linode/instances/123/configs

